import React, { useState, useEffect } from 'react';

function App() {
  const [match, setMatch] = useState(null);
  const [loading, setLoading] = useState(true);

  const apiUrl = 'https://www.thesportsdb.com/api/v1/json/123/searchevents.php?e=Arsenal_vs_Chelsea';

  useEffect(() => {
    fetch(apiUrl)
      .then(res => res.json())
      .then(data => {
        if (data.event && data.event.length > 0) {
          setMatch(data.event[0]);
        }
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching data:', err);
        setLoading(false);
      });
  }, []);

  return (
    <div style={{ fontFamily: 'Arial', margin: '20px' }}>
      <h1>Upcoming Match: Arsenal vs Chelsea</h1>
      {loading && <p>Loading match details...</p>}
      {match ? (
        <div style={{ border: '1px solid #ccc', padding: '20px', borderRadius: '8px', maxWidth: '400px' }}>
          <img src={match.strPoster} alt="Match Poster" style={{ width: '100%', borderRadius: '8px' }} />
          <h2>{match.strHomeTeam} vs {match.strAwayTeam}</h2>
          <p><strong>Date:</strong> {match.dateEvent}</p>
          <p><strong>Time:</strong> {match.strTimeLocal} (Local)</p>
          <img src={match.strHomeTeamBadge} alt="Home Badge" width="80" />
          <img src={match.strAwayTeamBadge} alt="Away Badge" width="80" />
          <p><strong>Venue:</strong> {match.strVenue}, {match.strCountry}</p>
          <p><strong>Status:</strong> {match.strStatus}</p>
          {match.strVideo && (
            <p>
              <a href={match.strVideo} target="_blank" rel="noreferrer">Watch Highlights</a>
            </p>
          )}
        </div>
      ) : !loading ? (
        <p>No match details found.</p>
      ) : null}
    </div>
  );
}

export default App;
